import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("Login").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def token = holder.accessToken
//Setting a varible in project level with the value
context.testCase.TestSuite.project.setPropertyValue("token","bearer "+token)

import groovy.json.JsonSlurper
def kumar = context.testCase.getTestStepByName("Get all users").getPropertyValue("Response")

def holder = new JsonSlurper().parseText(kumar)
def num = holder.count.toInteger()
log.info num
for(def i=0;i<num;i++)
{
	if(holder.users[i].email=="jaya0907.g@gmail.com")
	{
		def uid = holder.users[i]._id
		log.info uid
		def converted_uid = uid.toString()
		log.info converted_uid
		context.testCase.testSuite.project.setPropertyValue("user_Id",converted_uid)

	}
}

import groovy.json.JsonSlurper
def siva = context.testCase.getTestStepByName("Get all products").getPropertyValue("Response")

def holder = new JsonSlurper().parseText(siva)
def num = holder.count.toInteger()
log.info num
for(def i=0;i<num;i++)
{
	if(holder.products[i].name=="video game")
	{
		def pid = holder.products[i]._id
		log.info pid
		def converted_pid = pid.toString()
		log.info converted_pid
		context.testCase.testSuite.project.setPropertyValue("Product_Id",converted_pid)

	}
}

import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("Get all orders").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def num = holder.count.toInteger()
log.info num
for(def i=0;i<num;i++)
{
	if(holder.orders[i].product)
	{
		def name=holder.orders[i].product.name;
		if(name=="machine")
		{
		      def oid = holder.orders[i]._id;
		      def orderid = oid.toString()
		context.testCase.TestSuite.project.setPropertyValue("O_id", orderid)
	}
}
}
